use std::str;fn main(){let mut i=String::new();std::io::stdin().read_line(&mut i).unwrap();println!("Input: {}",i);println!("Output: {}",String::from_utf8(i.trim().chars().filter(|&c|c!='=').fold(Vec::new(),|mut a,c|{a.extend(format!("{:06b}","ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/".find(c).unwrap()).bytes());a}).chunks(8).map(|c|{u8::from_str_radix(str::from_utf8(c).unwrap(),2).unwrap()}).collect()).unwrap());}